import React from 'react';

const Days = (props) =>{
  const {days, icon} = props;
  return (
    <>
      {
        days.length && (
          days.map((day,i)=>(
      <div id={i} className='Days'>
        <h1>{day.applicable_date}</h1>
        <div>
          <img src={icon ? icon: null} alt="no load"/>
        </div>
        <div>
          <h4>{day.min_temp}</h4>
          <h4>{day.max_temp}</h4>
        </div>
      </div>
            )
          )
        )
      }
    </>
  );
};

export default Days;
