const axios =require('axios');

const api = {
  base: 'https://www.metaweather.com/api/location/search/?query=',
  location:'https://www.metaweather.com/api/location/'
};

const getLocation = async (location) => {
  try {
    const response = await axios.get(`${api.base}${location}`);
    console.log(response.data[0].woeid);
    const response2 = await axios.get(`${api.location}${response.data[0].woeid}`);
    console.log(response2.data);
    return response2.data;
  } catch (error) {
    console.error(error);
  }
}

export default getLocation;
