  const [state, setState] = useState({});

  const search = (e) =>{
    if(e.key === 'Enter'){
      const string = e.target.value;
      const toSearch = string.charAt(0).toUpperCase() + string.slice(1);
      const wheatherData = getLocation(toSearch);
    }
  }
  return (
    <div className="App">
      <header className="App-header">
        <Search searchEvent={search}/>

      </header>
    </div>
  );
}

export default App;
